package AutomationPractice;

public class ExceptionHandling {
	

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a =1;
		int b=0;
		try {
			System.out.println(a/b);
		} catch (Exception e) {
			// TODO Auto-generated catch block
			
			System.out.println(e);
		}

	}

}
